import React, { useState } from "react";
import {
  Grid,
  Card,
  CardContent,
  CardMedia,
  Typography,
  Modal,
  Box,
  Badge,
  Button,
  TextField,
  InputAdornment,
  IconButton,
  AppBar,
  Toolbar,
} from "@mui/material";
import { Search, FilterList, ShoppingCart, RemoveShoppingCart } from "@mui/icons-material";
import { useNavigate } from "react-router-dom";

const Marketplace = () => {
  const [searchQuery, setSearchQuery] = useState("");
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [openModal, setOpenModal] = useState(false);
  const [cart, setCart] = useState([]);
  const [cartOpen, setCartOpen] = useState(false);
  const products = [
    { id: 1, name: "Bluetooth Headphones", price: 49.99, image: "https://blaupunktaudio.in/cdn/shop/files/bh31-bk-wireless-headphones-.0.2.jpg?v=1723527502" },
    { id: 2, name: "Smartphone 5G", price: 699.99, image: "https://image01-in.oneplus.net/media/202503/25/a8f8fdbe9febb652286af072a5429666.png?x-amz-process=image/format,webp/quality,Q_80" },
    { id: 3, name: "Gaming Mouse", price: 29.99, image: "data:image/jpeg;base64," },
    { id: 4, name: "Mechanical Keyboard", price: 89.99, image: "data:image/jpeg;base64," },
    { id: 5, name: "Smartwatch", price: 199.99, image: "data:image/jpeg;base64," },
    { id: 6, name: "Wireless Speaker", price: 79.99, image: "data:image/jpeg;base64," },
    { id: 7, name: "Laptop Stand", price: 39.99, image: "data:image/jpeg;base64," },
    { id: 8, name: "Portable SSD", price: 129.99, image: "data:image/jpeg;base64," },
  ];

  const filteredProducts = products.filter((product) =>
    product.name.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const handleOpenModal = (product) => {
    setSelectedProduct(product);
    setOpenModal(true);
  };

  const handleCloseModal = () => {
    setOpenModal(false);
  };
  const addToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  // Remove product from cart
  const removeFromCart = (productId) => {
    setCart((prevCart) => prevCart.filter((item) => item.id !== productId));
  };
  return (
    <div style={{ backgroundColor: "#1B263B", minHeight: "92vh", color: "white", padding: "20px" }}>
      {/* Header with Search Bar and Icons */}
      <AppBar position="static" sx={{ backgroundColor: "#1B263B", padding: "10px", boxShadow: "none" }}>
        <Toolbar>
          <Typography variant="h4" sx={{ flexGrow: 1, fontWeight: "bold" }}>
            Marketplace
          </Typography>
          <TextField
            variant="outlined"
            placeholder="Search Marketplace"
            size="small"
            sx={{ backgroundColor: "#2A3B5D", borderRadius: "8px", input: { color: "white" }, width: "40%" }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <Search sx={{ color: "gray" }} />
                </InputAdornment>
              ),
            }}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <IconButton sx={{ color: "white", marginLeft: "10px" }}>
            <FilterList style={{ width: "50px" }} />
          </IconButton>
          <IconButton sx={{ color: "white", marginLeft: "10px" }} onClick={() => setCartOpen(true)}>
            <Badge badgeContent={cart.length} color="error">
              <ShoppingCart style={{ width: "50px" }} />
            </Badge>
          </IconButton>
        </Toolbar>
      </AppBar>

      {/* Product Grid */}
      <Grid container spacing={3} sx={{ marginTop: "20px" }}>
        {filteredProducts.map((product) => (
          <Grid item key={product.id} xs={12} sm={6} md={4} lg={3}>
            <Card
              sx={{
                backgroundColor: "#2A3B5D",
                boxShadow: 3,
                borderRadius: "12px",
                cursor: "pointer",
                "&:hover": { transform: "scale(1.05)", transition: "0.3s" },
              }}
              onClick={() => handleOpenModal(product)}
            >
              <CardMedia
                component="img"
                height="140"
                image={product.image}
                alt={product.name}
                sx={{
                  borderTopLeftRadius: "12px",
                  borderTopRightRadius: "12px",
                }}
              />
              <CardContent>
                <Typography variant="h6" sx={{ color: "white", fontWeight: "bold" }}>
                  {product.name}
                </Typography>
                <Typography variant="body1" color="gray">
                  ${product.price}
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        ))}
      </Grid>

      {/* Product Modal */}
      <Modal open={openModal} onClose={handleCloseModal}>
        <Box
          sx={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            width: 400,
            backgroundColor: "#1B263B",
            color: "white",
            padding: 4,
            borderRadius: "12px",
            boxShadow: 24,
          }}
        >
          {selectedProduct && (
            <>
              <CardMedia
                component="img"
                height="200"
                image={selectedProduct.image}
                alt={selectedProduct.name}
                sx={{ borderRadius: "8px" }}
              />
              <Typography variant="h5" fontWeight="bold" sx={{ marginTop: 2 }}>
                {selectedProduct.name}
              </Typography>
              <Typography variant="h6" color="lightgray" sx={{ marginTop: "10px" }}>
                ${selectedProduct.price}
              </Typography>
              <Typography variant="body1" sx={{ marginTop: "10px", color: "gray" }}>
                {selectedProduct.description}
              </Typography>

              {/* Buttons */}
              <Box sx={{ marginTop: "20px", textAlign: "center" }}>
                <Button variant="contained" sx={{ backgroundColor: "#238636", "&:hover": { backgroundColor: "#2EA043" }, marginRight: "10px" }} onClick={() => addToCart(selectedProduct)}>
                  Add to Cart
                </Button>
                <Button variant="contained" sx={{ backgroundColor: "#FF9800", "&:hover": { backgroundColor: "#E68900" } }}>
                  Buy Now
                </Button>
              </Box>

              {/* Close Button */}
              <Button onClick={handleCloseModal} sx={{ marginTop: "20px", color: "lightgray", textDecoration: "underline", display: "block", marginLeft: "auto", marginRight: "auto" }}>
                Close
              </Button>
            </>
          )}
        </Box>
      </Modal>

      {/* Cart Modal */}
      <Modal open={cartOpen} onClose={() => setCartOpen(false)}>
        <Box sx={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", width: 400, backgroundColor: "#1B263B", color: "white", padding: 4, borderRadius: "12px", boxShadow: 24 }}>
          <Typography variant="h5" fontWeight="bold">Cart</Typography>
          {cart.length === 0 ? <Typography sx={{ marginTop: "10px" }}>Your cart is empty.</Typography> : cart.map((item) => (
            <Box key={item.id} sx={{ display: "flex", justifyContent: "space-between", marginTop: "10px" }}>
              <Typography>{item.name} - ${item.price}</Typography>
              <IconButton onClick={() => removeFromCart(item.id)} sx={{ color: "red" }}>
                <RemoveShoppingCart />
              </IconButton>
            </Box>
          ))}
        </Box>
      </Modal>
    </div>
  );
};

export default Marketplace;
